mapper.insert:
	/**
 	* Query ID : insertAgentInfo
 	* Description : Agent 설치 등록
 	* Author : 박태혁
 	**/
 	INSERT INTO T_AGTCNDT_I
 	(
		AGT_SN
		, IPADR
		, SOCKET_PORT
		, AGT_CNDT_CD
		, AGT_VERSION
		, STRT_DTM
		, ISTCNF_YN
		, FRST_REGR_ID
		, FRST_REG_DTM
		, LST_MDFR_ID
		, LST_MDF_DTM
		, INTL_IPADR
 	)
 	VALUES
 	(
		NEXTVAL('Q_AGTCNDT_I_01')
		, #{IPADR}
		, #{SOCKET_PORT}
		, #{AGT_CNDT_CD}
		, #{AGT_VERSION}
		, now()
		, #{ISTCNF_YN}
		, #{FRST_REGR_ID}
		, now()
		, #{LST_MDFR_ID}
		, now()
		, #{INTL_IPADR}
 	)

